window.addGoogleFonts=e=>{if(typeof e!=="undefined"){if(e.trim()!==""){let t=[];let l=[];let o=[];e=e.replaceAll(",,",",").replaceAll(", ",",").replaceAll(" ,",",").replaceAll(" , ",",");let n=e.split(" ");for(let s=0;s<n.length;s++){n[s]=n[s].charAt(0).toUpperCase()+n[s].slice(1)}e=n.join(" ");if(e.indexOf(",")>-1){if(e.slice(-1)==","){e=e.slice(0,-1)}t=e.split(",")}else{t.push(e)}for(let i=0;i<t.length;i++){t[i]=t[i].trim().replaceAll(" ","+")}if(!document.querySelector("link[rel='preconnect'][href*='//fonts.googleapis.com']")){let h=document.createElement("link");h.rel="preconnect";h.href="https://fonts.googleapis.com";document.head.append(h)}if(!document.querySelector("link[rel='preconnect'][href*='//fonts.gstatic.com'][crossorigin]")){let f=document.createElement("link");f.rel="preconnect";f.href="https://fonts.gstatic.com";f.crossOrigin="";document.head.append(f)}let c=[100,200,300,400,500,600,700,800,900];for(let a of t){let r=[];fetch(`https://fonts.googleapis.com/css2?family=${a}`).then((e=>{l.push(a);for(let e of c){fetch(`https://fonts.googleapis.com/css2?family=${a}:wght@${e}`).then((t=>{r.push(e);if(r.length&&r.length==c.length){commencer()}})).catch((e=>{r.push("no");if(r.length&&r.length==c.length){commencer()}}))}function commencer(){r=r.filter((e=>!isNaN(e))).sort(((e,t)=>e-t));if(r.length==1){let e=`https://fonts.googleapis.com/css2?family=${a}:ital,wght@0,${r[0]};1,${r[0]}`;fetch(e).then((e=>{o.push(`${a}:ital,wght@0,${r[0]};1,${r[0]}`);fontsCombined()})).catch((e=>{}))}else{let e=r.reduce(((e,t)=>Math.min(e,t)));let t=r.reduce(((e,t)=>Math.max(e,t)));let l=`https://fonts.googleapis.com/css2?family=${a}:ital,wght@0,${e}..${t};1,${e}..${t}`;fetch(l).then((l=>{o.push(`${a}:ital,wght@0,${e}..${t};1,${e}..${t}`);fontsCombined()})).catch((e=>{let t=[...r];let l=[...r];for(let e=0;e<t.length;e++){t[e]=`0,${t[e]}`}t=t.join(";");for(let e=0;e<l.length;e++){l[e]=`1,${l[e]}`}l=l.join(";");let n=[t,l].join(";");let c=`https://fonts.googleapis.com/css2?family=${a}:ital,wght@${n}`;fetch(c).then((e=>{o.push(`${a}:ital,wght@${n}`);fontsCombined()})).catch()}))}}})).catch((e=>{console.log(`${a} does not exist on Google Fonts.`)}));function fontsCombined(){if(o.length==l.length){let e=`https://fonts.googleapis.com/css2?family=${o.join("&family=")}&display=swap`;let t=document.createElement("link");t.href=e;t.rel="stylesheet";document.head.append(t)}}}}}else{}};
